import React, { useMemo } from 'react';
import ReactFlow, { 
  Controls, 
  Background,
  MarkerType,
} from 'react-flow-renderer';
import nodeTypes from './nodeTypes';

function VisualizationPanel({ nodes, edges, onNodesChange, onEdgesChange, onNodeDoubleClick }) {
  const edgesWithLabels = useMemo(() => {
    return edges.map(edge => {
      const sourceNode = nodes.find(node => node.id === edge.source);
      const targetNode = nodes.find(node => node.id === edge.target);
      let label = '';

      if (sourceNode.type === 'Customer' || targetNode.type === 'Customer') {
        label = `Customer ${targetNode.type === 'Transfer' || targetNode.type === 'BillPay' ? '→ ' + targetNode.type : ''}`;
      } else if (sourceNode.type === 'Counter-Party' || targetNode.type === 'Counter-Party') {
        label = `Counter-Party ${targetNode.type === 'Transfer' ? '→ Transfer' : ''}`;
      }

      return {
        ...edge,
        type: 'straight',
        animated: false,
        label,
        labelStyle: { fill: '#888', fontWeight: 700 },
        labelBgPadding: [8, 4],
        labelBgBorderRadius: 4,
        labelBgStyle: { fill: '#ffffff', color: '#888', fillOpacity: 0.7 },
        style: { stroke: '#888' },
        markerEnd: {
          type: MarkerType.ArrowClosed,
          color: '#888',
        },
      };
    });
  }, [edges, nodes]);

  return (
    <div className="visualization-panel">
      {nodes.length > 0 ? (
        <ReactFlow 
          nodes={nodes}
          edges={edgesWithLabels}
          onNodesChange={onNodesChange}
          onEdgesChange={onEdgesChange}
          nodeTypes={nodeTypes}
          onNodeDoubleClick={onNodeDoubleClick}
          fitView
          style={{ width: '100%', height: '100%' }}
          defaultViewport={{ x: 0, y: 0, zoom: 1.5 }}
          nodesDraggable={true}
          elementsSelectable={false}
          minZoom={0.5}
          maxZoom={2}
        >
          <Controls />
          <Background color="#f0f0f0" gap={16} />
        </ReactFlow>
      ) : (
        <p>Select a group to view its visualization</p>
      )}
    </div>
  );
}

export default VisualizationPanel;
