import React, { useState, useEffect } from 'react';
import './App.css';

const API_BASE_URL = 'http://localhost:3001/api';

function App() {
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [selectedGroup, setSelectedGroup] = useState(null);
  const [groupDetails, setGroupDetails] = useState(null);
  const [graphData, setGraphData] = useState(null);

  const handleSearch = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(`${API_BASE_URL}/groups/search?term=${searchTerm}`);
      const data = await response.json();
      setSearchResults(data);
    } catch (error) {
      console.error('Failed to search groups:', error);
    }
  };

  const handleGroupSelect = async (groupId) => {
    setSelectedGroup(groupId);
    try {
      const [detailsResponse, graphResponse] = await Promise.all([
        fetch(`${API_BASE_URL}/groups/${groupId}`),
        fetch(`${API_BASE_URL}/groups/${groupId}/graph`)
      ]);
      const details = await detailsResponse.json();
      const graph = await graphResponse.json();
      setGroupDetails(details);
      setGraphData(graph);
    } catch (error) {
      console.error('Failed to fetch group data:', error);
    }
  };

  return (
    <div className="app-container">
      <header className="app-header">
        <h1>Entity Resolution System</h1>
      </header>
      <div className="app-content">
        <div className="search-panel">
          <form onSubmit={handleSearch}>
            <input
              type="text"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              placeholder="Search groups..."
              className="search-input"
            />
            <button type="submit" className="search-button">Search</button>
          </form>
          <div className="search-results">
            {searchResults.map(groupId => (
              <div key={groupId} onClick={() => handleGroupSelect(groupId)} className="search-result-item">
                {groupId}
              </div>
            ))}
          </div>
        </div>
        <div className="visualization-panel">
          {graphData ? (
            <div className="graph-container">
              {/* TODO: Implement graph visualization using graphData */}
              <p>Graph visualization for group: {selectedGroup}</p>
              <pre>{JSON.stringify(graphData, null, 2)}</pre>
            </div>
          ) : (
            <p>Select a group to view its visualization</p>
          )}
        </div>
        <div className="details-panel">
          <h2>Group Details</h2>
          {groupDetails ? (
            <pre>{JSON.stringify(groupDetails, null, 2)}</pre>
          ) : (
            <p>Select a group to view details</p>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
