import React, { useState } from 'react';

function SearchPanel({ allGroups, onGroupSelect, selectedGroup }) {
  const [showGroupList, setShowGroupList] = useState(true);
  const [investigationNotes, setInvestigationNotes] = useState('');

  const handleGroupSelect = (groupId) => {
    onGroupSelect(groupId);
    setShowGroupList(false);
  };

  return (
    <div className="search-panel">
      {showGroupList ? (
        <>
          <button onClick={() => setShowGroupList(false)}>Hide Group List</button>
          <div className="group-list">
            {allGroups.map((group) => (
              <div
                key={group.id}
                className="group-item"
                onClick={() => handleGroupSelect(group.id)}
              >
                {group.name}
              </div>
            ))}
          </div>
        </>
      ) : (
        <>
          <button onClick={() => setShowGroupList(true)}>Show Group List</button>
          <div className="scoring-data">
            <h3>Scoring Data</h3>
            {/* Add scoring data here when available */}
          </div>
          <div className="investigation-notes">
            <h3>Investigation Notes</h3>
            <textarea
              value={investigationNotes}
              onChange={(e) => setInvestigationNotes(e.target.value)}
              placeholder="Enter your investigation notes here..."
            />
          </div>
        </>
      )}
    </div>
  );
}

export default SearchPanel;
