import React, { useState, useEffect } from 'react';

const Dropdown = ({ label, isOpen, toggleOpen, children }) => (
  <div className="dropdown">
    <h3 onClick={toggleOpen} style={{ cursor: 'pointer' }}>
      {isOpen ? '▼' : '▶'} {label}
    </h3>
    {isOpen && children}
  </div>
);

const EntityTypeDropdown = ({ type, entities }) => {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <Dropdown label={`${type} (${entities.length})`} isOpen={isOpen} toggleOpen={() => setIsOpen(!isOpen)}>
      <ul>
        {entities.map((entity, index) => (
          <li key={index}>{entity.displayName || entity.id}</li>
        ))}
      </ul>
    </Dropdown>
  );
};

const DataTypeDropdown = ({ label, data }) => {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <Dropdown label={label} isOpen={isOpen} toggleOpen={() => setIsOpen(!isOpen)}>
      <ul>
        {Object.entries(data).map(([key, value]) => (
          <li key={key}>{`${key}: ${value}`}</li>
        ))}
      </ul>
    </Dropdown>
  );
};

function EntityDetails({ nodes }) {
  const [entityTypes, setEntityTypes] = useState({});
  const [personalDataTypes, setPersonalDataTypes] = useState({});
  const [isEntityTypesOpen, setIsEntityTypesOpen] = useState(false);
  const [isPersonalDataTypesOpen, setIsPersonalDataTypesOpen] = useState(false);

  useEffect(() => {
    const types = {};
    const dataTypes = {};

    nodes.forEach(node => {
      if (!types[node.type]) {
        types[node.type] = [];
      }
      types[node.type].push(node.data);

      if (node.data.info) {
        Object.keys(node.data.info).forEach(key => {
          if (!dataTypes[key]) {
            dataTypes[key] = new Set();
          }
          dataTypes[key].add(node.data.info[key]);
        });
      }
    });

    setEntityTypes(types);
    setPersonalDataTypes(dataTypes);
  }, [nodes]);

  return (
    <div className="details-panel">
      <h2>Entity Details</h2>
      <Dropdown 
        label="Entity Types" 
        isOpen={isEntityTypesOpen} 
        toggleOpen={() => setIsEntityTypesOpen(!isEntityTypesOpen)}
      >
        <div className="entity-types">
          {Object.entries(entityTypes).map(([type, entities]) => (
            <EntityTypeDropdown key={type} type={type} entities={entities} />
          ))}
        </div>
      </Dropdown>
      <Dropdown 
        label="Personal Data Types" 
        isOpen={isPersonalDataTypesOpen} 
        toggleOpen={() => setIsPersonalDataTypesOpen(!isPersonalDataTypesOpen)}
      >
        <div className="personal-data-types">
          {Object.entries(personalDataTypes).map(([label, values]) => (
            <DataTypeDropdown 
              key={label} 
              label={label} 
              data={Object.fromEntries([...values].map((value, index) => [index + 1, value]))}
            />
          ))}
        </div>
      </Dropdown>
    </div>
  );
}

export default EntityDetails;
