import React, { useState } from 'react';

const TimeframeSelector = ({ startDate, endDate, onStartDateChange, onEndDateChange, onApplyFilter }) => {
  const [localStartDate, setLocalStartDate] = useState(startDate);
  const [localEndDate, setLocalEndDate] = useState(endDate);

  const handleApplyFilter = () => {
    onStartDateChange(localStartDate);
    onEndDateChange(localEndDate);
    setTimeout(() => {
      onApplyFilter();
    }, 0);
  };

  const handleReset = () => {
    const resetValue = '';
    setLocalStartDate(resetValue);
    setLocalEndDate(resetValue);
    onStartDateChange(resetValue);
    onEndDateChange(resetValue);
    setTimeout(() => {
      onApplyFilter();
    }, 0);
  };

  return (
    <div className="timeframe-selector">
      <label>
        Start Date:
        <input
          type="date"
          value={localStartDate}
          onChange={(e) => setLocalStartDate(e.target.value)}
        />
      </label>
      <label>
        End Date:
        <input
          type="date"
          value={localEndDate}
          onChange={(e) => setLocalEndDate(e.target.value)}
        />
      </label>
      <button onClick={handleApplyFilter}>Apply</button>
      <button onClick={handleReset}>Reset</button>
    </div>
  );
};

export default TimeframeSelector;
