import React, { useState, useCallback, useEffect } from 'react';
import ReactFlow, { 
  useNodesState, 
  useEdgesState, 
  applyNodeChanges, 
  applyEdgeChanges 
} from 'react-flow-renderer';
import SearchPanel from './SearchPanel';
import VisualizationPanel from './VisualizationPanel';
import EntityDetails from './EntityDetails';
import NodeInfoPopup from './NodeInfoPopup';
import ToolBar from './ToolBar';
import TabNavigation from './TabNavigation';
import TimeframeSelector from './TimeframeSelector';
import './App.css';
import { MarkerType } from 'react-flow-renderer';

const API_BASE_URL = 'http://localhost:3001/api';

const UserIcon = () => (
  <svg className="header-icon" viewBox="0 0 24 24" width="24" height="24" fill="currentColor">
    <path d="M12 12c2.21 0 4-1.79 4-4s-1.79-4-4-4-4 1.79-4 4 1.79 4 4 4zm0 2c-2.67 0-8 1.34-8 4v2h16v-2c0-2.66-5.33-4-8-4z" />
  </svg>
);

const MenuIcon = () => (
  <svg className="header-icon" viewBox="0 0 24 24" width="24" height="24" fill="currentColor">
    <path d="M3 18h18v-2H3v2zm0-5h18v-2H3v2zm0-7v2h18V6H3z" />
  </svg>
);

function App() {
  const [allGroups, setAllGroups] = useState([]);
  const [openGroups, setOpenGroups] = useState([]);
  const [activeGroupId, setActiveGroupId] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [selectedNode, setSelectedNode] = useState(null);

  const [timeframe, setTimeframe] = useState({ startDate: '', endDate: '' });
  const [appliedTimeframe, setAppliedTimeframe] = useState({ startDate: '', endDate: '' });


  const handleNodeChanges = useCallback((changes, groupId) => {
    setOpenGroups(prev => prev.map(group => 
      group.id === groupId 
        ? { ...group, nodes: applyNodeChanges(changes, group.nodes) }
        : group
    ));
  }, []);

  const handleEdgeChanges = useCallback((changes, groupId) => {
    setOpenGroups(prev => prev.map(group => 
      group.id === groupId 
        ? { ...group, edges: applyEdgeChanges(changes, group.edges) }
        : group
    ));
  }, []);

  useEffect(() => {
    fetchAllGroups();
  }, []);

  const fetchAllGroups = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/groups`);
      const data = await response.json();
      setAllGroups(Array.isArray(data) ? data : []);
    } catch (error) {
      console.error('Failed to fetch all groups:', error);
      setError('Failed to fetch groups. Please try again later.');
    }
  };

  const handleGroupSelect = useCallback(async (groupId) => {
    if (openGroups.some(group => group.id === groupId)) {
      setActiveGroupId(groupId);
      return;
    }

    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch(`${API_BASE_URL}/groups/${groupId}/data`);
      const data = await response.json();

      if (data.graphData && Array.isArray(data.graphData.nodes) && Array.isArray(data.graphData.edges)) {
        const newNodes = data.graphData.nodes
          .filter(node => node && node.type)
          .map(node => ({
            ...node,
            position: getInitialNodePosition(node.type),
          }));

        const newEdges = data.graphData.edges
          .filter(edge => edge && edge.source && edge.target)
          .map(edge => ({
            ...edge,
            type: 'smoothstep',
            animated: true,
            style: { stroke: '#888' },
            markerEnd: {
              type: MarkerType.ArrowClosed,
              width: 20,
              height: 20,
              color: '#888',
            },
          }));

        setOpenGroups(prev => [...prev, { id: groupId, data, nodes: newNodes, edges: newEdges }]);
        setActiveGroupId(groupId);
      } else {
        console.error('Unexpected data structure:', data);
        setError('Received unexpected data structure from the server.');
      }
    } catch (error) {
      console.error('Failed to fetch group data:', error);
      setError('Failed to fetch group data. Please try again later.');
    } finally {
      setIsLoading(false);
    }
  }, [openGroups]);

  const handleCloseGroup = useCallback((groupId) => {
    setOpenGroups(prev => prev.filter(group => group.id !== groupId));
    if (activeGroupId === groupId) {
      setActiveGroupId(openGroups[0]?.id || null);
    }
  }, [activeGroupId, openGroups]);

  const getInitialNodePosition = (type) => {
    const centerX = 250;
    const centerY = 250;
    const radius = 200;
    const angle = Math.random() * 2 * Math.PI;

    switch(type) {
      case 'Customer':
        return { x: centerX + radius * Math.cos(angle), y: centerY + radius * Math.sin(angle) };
      case 'Counter-Party':
        return { x: centerX + radius * 0.8 * Math.cos(angle + Math.PI), y: centerY + radius * 0.6 * Math.sin(angle + Math.PI) };
      case 'Business':
        return { x: centerX + radius * 0.6 * Math.cos(angle + Math.PI/2), y: centerY + radius * 0.6 * Math.sin(angle + Math.PI/2) };
      case 'Transfer':
      case 'BillPay':
        return { x: centerX + radius * 0.4 * Math.cos(angle), y: centerY + radius * 0.4 * Math.sin(angle) };
      default:
        return { x: centerX + radius * Math.random(), y: centerY + radius * Math.random() };
    }
  };

  const handleNodeDoubleClick = useCallback((event, node) => {
    setSelectedNode(node);
  }, []);

  const activeGroup = openGroups.find(group => group.id === activeGroupId);

  const filterNodesByTimeframe = (nodes, startDate, endDate) => {
    if (!startDate && !endDate) return nodes;

    const parseDate = (dateString) => {
      if (!dateString) return null;
      const [datePart, timePart] = dateString.split('-');
      if (!datePart || !timePart) return null;
      const [month, day, year] = datePart.split('/');
      const [hours, minutes] = timePart.split(':');
      return new Date(2000 + parseInt(year), parseInt(month) - 1, parseInt(day), parseInt(hours), parseInt(minutes));
    };

    const start = startDate ? new Date(startDate) : null;
    const end = endDate ? new Date(endDate) : null;

    return nodes.filter(node => {
      let transactionDate;

      if (node.type === 'Transfer' || node.type === 'BillPay') {
        transactionDate = node.data?.transaction?.split('_')[1];
      } else if (node.data?.timestamp) {
        transactionDate = node.data.timestamp;
      } else {
        return true; // Keep nodes without a recognizable date
      }

      if (!transactionDate) return true;

      try {
        const nodeDate = parseDate(transactionDate);
        if (!nodeDate) return true; // Keep nodes with unparseable dates

        if (start && end) {
          return nodeDate >= start && nodeDate <= end;
        } else if (start) {
          return nodeDate >= start;
        } else if (end) {
          return nodeDate <= end;
        }
      } catch (error) {
        console.error('Error parsing date:', transactionDate);
        return true; // Include nodes with unparseable dates
      }
      return true;
    });
  };

  const filterEdgesByTimeframe = (edges, nodes) => {
    const nodeIds = new Set(nodes.map(n => n.id));
    return edges.filter(edge => nodeIds.has(edge.source) && nodeIds.has(edge.target));
  };

  const applyTimeframeFilter = () => {
    if (!timeframe.startDate && !timeframe.endDate) {
      alert("Please enter at least one date to apply the filter.");
      return;
    }
    setAppliedTimeframe(timeframe);

    if (activeGroup && activeGroup.nodes && activeGroup.edges) {
      const filteredNodes = filterNodesByTimeframe(activeGroup.nodes, timeframe.startDate, timeframe.endDate);
      const filteredEdges = filterEdgesByTimeframe(activeGroup.edges, filteredNodes);

      setOpenGroups(prev => prev.map(group => 
        group.id === activeGroupId 
          ? { 
              ...group, 
              nodes: filteredNodes, 
              edges: filteredEdges,
              data: {
                ...group.data,
                graphData: {
                  nodes: filteredNodes,
                  edges: filteredEdges,
                },
              },
            }
          : group
      ));
    }
  };

  const filteredActiveGroup = activeGroup ? {
    ...activeGroup,
    nodes: filterNodesByTimeframe(activeGroup.nodes, appliedTimeframe.startDate, appliedTimeframe.endDate),
    edges: filterEdgesByTimeframe(activeGroup.edges, activeGroup.nodes),
    data: {
      ...activeGroup.data,
      graphData: {
        nodes: filterNodesByTimeframe(activeGroup.nodes, appliedTimeframe.startDate, appliedTimeframe.endDate),
        edges: filterEdgesByTimeframe(activeGroup.edges, activeGroup.nodes),
      },
    },
  } : null;
  return (
    <div className="app-container">
      <header className="app-header">
        <div className="app-icon-container">
          <img src="icons/GTIcon.png" alt="Entity Resolution" className="app-icon" />
        </div>
        <div className="header-icons">
          <UserIcon />
          <MenuIcon />
        </div>
      </header>
      <TabNavigation 
        openGroups={openGroups}
        activeGroupId={activeGroupId}
        onTabChange={setActiveGroupId}
        onCloseTab={handleCloseGroup}
      />
      <div className="app-content">
        <SearchPanel 
          allGroups={allGroups} 
          onGroupSelect={handleGroupSelect} 
          selectedGroup={activeGroupId}
        />
        {isLoading ? (
          <div className="loading-indicator">Loading...</div>
        ) : error ? (
          <div className="error-message">{error}</div>
        ) : activeGroup ? (
          <div className="active-group-content">
            <div className="tabs-container">
              {/* Tabs here */}
            </div>
            <div className="visualization-container">
              <VisualizationPanel 
                nodes={filteredActiveGroup.nodes}
                edges={filteredActiveGroup.edges}
                onNodesChange={(changes) => handleNodeChanges(changes, activeGroupId)}
                onEdgesChange={(changes) => handleEdgeChanges(changes, activeGroupId)}
                onNodeDoubleClick={handleNodeDoubleClick}
              />
              <ToolBar />
              <EntityDetails groupData={filteredActiveGroup.data} />
            </div>
            <div className="timeframe-selector">
              <TimeframeSelector
                startDate={timeframe.startDate}
                endDate={timeframe.endDate}
                onStartDateChange={(date) => setTimeframe(prev => ({ ...prev, startDate: date }))}
                onEndDateChange={(date) => setTimeframe(prev => ({ ...prev, endDate: date }))}
                onApplyFilter={applyTimeframeFilter}
              />
            </div>
          </div>
        ) : (
          <div className="no-group-selected">Select a group to view details</div>
        )}
      </div>
      {selectedNode && (
        <NodeInfoPopup node={selectedNode} onClose={() => setSelectedNode(null)} />
      )}
    </div>
  );



}

export default App;
