import React, { useState, useEffect, useCallback } from 'react';
import ReactFlow, { MiniMap, Controls, Background, useNodesState, useEdgesState } from 'react-flow-renderer';
import { 
  forceSimulation, 
  forceLink, 
  forceManyBody, 
  forceCenter 
} from 'd3-force';
import './App.css';
import './App.css';

const API_BASE_URL = 'http://localhost:3001/api';



function App() {
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [allGroups, setAllGroups] = useState([]);
  const [selectedGroup, setSelectedGroup] = useState(null);
  const [groupDetails, setGroupDetails] = useState(null);
  const [nodes, setNodes, onNodesChange] = useNodesState([]);
  const [edges, setEdges, onEdgesChange] = useEdgesState([]);
  const [graphData, setGraphData] = useState(null);
  const [elements, setElements] = useState([]);
  const [selectedNode, setSelectedNode] = useState(null);
  const [personalInfo, setPersonalInfo] = useState(null);
    // Custom node types
  const nodeTypes = {
    Customer: ({ data }) => (
      <div className="custom-node customer-node">
        <strong>{data.label}</strong>
        <div>Name: {data.FirstName} {data.LastName}</div>
        <div>Email: {data.Email}</div>
      </div>
    ),
    'Counter-Party': ({ data }) => (
      <div className="custom-node counter-party-node">
        <strong>{data.label}</strong>
      </div>
    ),
    Business: ({ data }) => (
      <div className="custom-node business-node">
        <strong>{data.label}</strong>
      </div>
    ),
    customNode: ({ data }) => {
      const nodeType = data.type ? data.type.toLowerCase() : 'default';
      return (
        <div 
          className={`custom-node ${nodeType}-node`}
          style={{ 
            width: data.selected ? '80px' : '60px', 
            height: data.selected ? '80px' : '60px',
            borderRadius: '50%',
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            transition: 'all 0.3s ease'
          }}
        >
          {data.selected && <div>{data.type || 'Node'}</div>}
        </div>
      );
    },
  };
  useEffect(() => {
    fetchAllGroups();
  }, []);

  const fetchAllGroups = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/groups`);
      const data = await response.json();
      setAllGroups(Array.isArray(data) ? data : []);
    } catch (error) {
      console.error('Failed to fetch all groups:', error);
    }
  };
  const onNodeClick = useCallback((event, node) => {
    setSelectedNode(node);
  }, []);


  const onNodeDoubleClick = useCallback(async (event, node) => {
    try {
      const response = await fetch(`${API_BASE_URL}/nodes/${node.id}/personal-info`);
      const data = await response.json();
      setPersonalInfo(data);
    } catch (error) {
      console.error('Failed to fetch personal info:', error);
    }
  }, []);
  const handleSearch = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(`${API_BASE_URL}/groups/search?term=${searchTerm}`);
      const data = await response.json();
      setSearchResults(Array.isArray(data) ? data : []);
    } catch (error) {
      console.error('Failed to search groups:', error);
    }
  };


  const handleGroupSelect = useCallback(async (groupId) => {
    setSelectedGroup(groupId);
    try {
      const [detailsResponse, graphResponse] = await Promise.all([
        fetch(`${API_BASE_URL}/groups/${groupId}`),
        fetch(`${API_BASE_URL}/groups/${groupId}/graph`)
      ]);
      const details = await detailsResponse.json();
      const graph = await graphResponse.json();
      setGroupDetails(details);

      const newNodes = [];
      const newEdges = [];

      graph.nodes.forEach(node => {
        newNodes.push({
          ...node,
          type: 'customNode',
          position: { x: 0, y: 0 },
          data: { ...node.data, selected: false }
        });

        // Create transaction nodes and edges
        node.data.transactions.forEach((transaction, index) => {
          const [type, targetId, amount] = transaction.split('_');
          if (type === 'send' || type === 'receive') {
            const transactionNodeId = `${node.id}-${type}-${index}`;
            newNodes.push({
              id: transactionNodeId,
              type: 'customNode',
              position: { x: 0, y: 0 },
              data: { type: 'Transaction', label: `${type} $${amount}`, selected: false }
            });

            newEdges.push({
              id: `e${node.id}-${transactionNodeId}`,
              source: node.id,
              target: transactionNodeId,
              type: 'default',
              animated: true,
            });

            newEdges.push({
              id: `e${transactionNodeId}-${targetId}`,
              source: transactionNodeId,
              target: targetId,
              type: 'default',
              animated: true,
            });
          }
        });
      });

      setNodes(newNodes);
      setEdges(newEdges);
    } catch (error) {
      console.error('Failed to fetch group data:', error);
    }
  }, [setNodes, setEdges]);

  useEffect(() => {
    if (nodes.length > 0 && edges.length > 0) {
      const simulation = forceSimulation(nodes)
        .force('link', forceLink(edges).id(d => d.id).distance(100))
        .force('charge', forceManyBody().strength(-300))
        .force('center', forceCenter(250, 250))
        .on('tick', () => {
          setNodes(nodes => nodes.map(node => ({
            ...node,
            position: { x: node.x, y: node.y }
          })));
        });

      return () => simulation.stop();
    }
  }, [nodes, edges, setNodes]);
  return (
    <div className="app-container">
      <header className="app-header">
        <h1>Entity Resolution System</h1>
      </header>
      <div className="app-content">
        <div className="search-panel">
          {allGroups.map(groupId => (
            <div 
              key={groupId} 
              onClick={() => handleGroupSelect(groupId)} 
              className={`group-item ${selectedGroup === groupId ? 'selected' : ''}`}
            >
              {groupId}
            </div>
          ))}
        </div>
        <div className="visualization-panel">
          {nodes.length > 0 ? (
            <ReactFlow 
              nodes={nodes}
              edges={edges}
              onNodesChange={onNodesChange}
              onEdgesChange={onEdgesChange}
              nodeTypes={nodeTypes}
              onNodeClick={onNodeClick}
              fitView
              style={{ width: '100%', height: '600px' }}
            >
              <MiniMap />
              <Controls />
              <Background color="#aaa" gap={16} />
            </ReactFlow>
          ) : (
            <p>Select a group to view its visualization</p>
          )}
        </div>
        <div className="details-panel">
          {selectedNode ? (
            <div>
              <h2>{selectedNode.data.type}: {selectedNode.id}</h2>
              {selectedNode.data.info && (
                <pre>{JSON.stringify(selectedNode.data.info, null, 2)}</pre>
              )}
            </div>
          ) : (
            <p>Select a node to view details</p>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
