import React from 'react';

function EntityDetails({ selectedNode }) {
  if (!selectedNode) {
    return <div className="details-panel"><p>Select a node to view details</p></div>;
  }

  const { type, data } = selectedNode;
  const { displayName, info } = data;

  return (
    <div className="details-panel">
      <h2>{type}: {displayName}</h2>
      <div className="entity-info">
        <h3>Details:</h3>
        {Object.entries(info).map(([key, value]) => (
          <div key={key} className="info-item">
            <span className="info-key">{key}:</span>
            <span className="info-value">{value}</span>
          </div>
        ))}
      </div>
      {type === 'Customer' && (
        <div className="transactions">
          <h3>Transactions:</h3>
          {/* You'll need to implement logic to fetch and display transactions */}
        </div>
      )}
    </div>
  );
}

export default EntityDetails;
