import React, { useState, useCallback, useEffect } from 'react';
import { useNodesState, useEdgesState } from 'react-flow-renderer';
import SearchPanel from './SearchPanel';
import VisualizationPanel from './VisualizationPanel';
import EntityDetails from './EntityDetails';
import './App.css';

const API_BASE_URL = 'http://localhost:3001/api';

function App() {
  const [allGroups, setAllGroups] = useState([]);
  const [selectedGroup, setSelectedGroup] = useState(null);
  const [nodes, setNodes, onNodesChange] = useNodesState([]);
  const [edges, setEdges, onEdgesChange] = useEdgesState([]);

  useEffect(() => {
    fetchAllGroups();
  }, []);

  const fetchAllGroups = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/groups`);
      const data = await response.json();
      setAllGroups(Array.isArray(data) ? data : []);
    } catch (error) {
      console.error('Failed to fetch all groups:', error);
    }
  };

  const handleGroupSelect = useCallback(async (groupId) => {
    setSelectedGroup(groupId);
    try {
      const response = await fetch(`${API_BASE_URL}/groups/${groupId}/graph`);
      const graph = await response.json();

      const newNodes = graph.nodes.map(node => ({
        ...node,
        position: getInitialNodePosition(node.type),
      }));

      const newEdges = graph.edges.map(edge => ({
        ...edge,
        type: 'smoothstep',
        animated: true,
        style: { stroke: '#888' },
      }));

      setNodes(newNodes);
      setEdges(newEdges);
    } catch (error) {
      console.error('Failed to fetch group data:', error);
    }
  }, [setNodes, setEdges]);

  const getInitialNodePosition = (type) => {
    const centerX = 250;
    const centerY = 250;
    const radius = 200;
    const angle = Math.random() * 2 * Math.PI;

    switch(type) {
      case 'Customer':
        return { x: centerX + radius * Math.cos(angle), y: centerY + radius * Math.sin(angle) };
      case 'Counter-Party':
        return { x: centerX + radius * 0.8 * Math.cos(angle + Math.PI), y: centerY + radius * 0.8 * Math.sin(angle + Math.PI) };
      case 'Business':
        return { x: centerX + radius * 0.6 * Math.cos(angle + Math.PI/2), y: centerY + radius * 0.6 * Math.sin(angle + Math.PI/2) };
      case 'Transfer':
      case 'BillPay':
        return { x: centerX + radius * 0.4 * Math.cos(angle), y: centerY + radius * 0.4 * Math.sin(angle) };
      default:
        return { x: centerX + radius * Math.random(), y: centerY + radius * Math.random() };
    }
  };

  const handleNodeDoubleClick = useCallback((event, node) => {
    setSelectedNode(node);
  }, []);

  return (
    <div className="app-container">
      <header className="app-header">
        <h1>Entity Resolution</h1>
        <div className="header-icons">
          <img src="/icons/account.png" alt="Account" className="header-icon" />
          <img src="/icons/menu.png" alt="Menu" className="header-icon" />
        </div>
      </header>
      <div className="app-content">
        <SearchPanel allGroups={allGroups} onGroupSelect={handleGroupSelect} />
        <VisualizationPanel 
          nodes={nodes}
          edges={edges}
          onNodesChange={onNodesChange}
          onEdgesChange={onEdgesChange}
          onNodeDoubleClick={handleNodeDoubleClick}
        />
        <EntityDetails nodes={nodes} />
      </div>
      {selectedNode && (
        <NodeInfoPopup node={selectedNode} onClose={() => setSelectedNode(null)} />
      )}
    </div>
  );
}


export default App;
